 p_id=>wwv_flow_api.id(14561083628889935)
,p_name=>'MAIN_MENU'
,p_list_type=>'SQL_QUERY'
,p_list_query=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT',
'level, ',
'MENU_NAME as title, ',
'''f?p=''||NVL(APP_ID,1010)||'':''||PAGE_ID||'':&APP_SESSION.::''||PAGE_ID||'':NO::'' as target, ',
'PAGE_ID is_current,',
'NVL(FA_CLASS,''fa-braille'') as image, ',
''''' image_attrib, ',
'MENU_NAME as tooltip',
'FROM(',
'SELECT ',
'ID,',
'MENU_NAME,',
'APP_ID,',
'PAGE_ID,',
'FA_CLASS,',
'PARENT_ID,',
'MENU_POSITION',
'FROM ISP_MAIN_MENU',
'WHERE PARENT_ID IS NULL',
'UNION',
'SELECT',
'M.ID,',
'MENU_NAME,',
'M.APP_ID,',
'M.PAGE_ID,',
'FA_CLASS,',
'PARENT_ID,',
'MENU_POSITION',
'from ISP_MAIN_MENU M,',
'ISP_GROUP_ACCESS A,',
'ISP_USER U,',
'ISP_USER_GROUP G',
'WHERE NVL(U.GROUP_ID,0)=G.ID',
'AND G.ID=A.GROUP_ID',
'AND M.APP_ID=A.APP_ID',
'AND M.PAGE_ID=A.PAGE_ID',
'AND UPPER(U.USER_NAME) = :APP_USER',
'AND PARENT_ID IS NOT NULL)',
'start with PARENT_ID is null',
'connect by prior  ID = PARENT_ID ',
'order siblings by nvl(MENU_POSITION,99) asc'))
,p_list_status=>'PUBLIC'
);
